package practice;

public class ReverseEquilateralTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=6;
		
		for(int i=num;i<=0;i--) {
			for(int j=num-i;j<=0;j--) {
				System.out.print(" ");
			}
			for(int j=i;j<=0;j--) {
				System.out.print("* ");
		}
			System.out.println();

	}
	}

}
